mapper.update:
update es_role_cluster_host
set ip = #{ip}
where role_cluster_id = #{roleClusterId} and hostname = #{hostname}

mapper.select:
select

from es_role_cluster_host

    and id = #{id}
    and role_cluster_id = #{roleClusterId}
    and `hostname` = #{hostname}
    and ip = #{ip}
    and cluster = #{cluster}
    and port = #{port}
    and `status` = #{status}
    and `role` = #{role}
    and rack = #{rack}
    and node_set = #{nodeSet}
    and delete_flag = 0

select

from es_role_cluster_host
where cluster = #{cluster} and rack = #{rack} and status = 1 and delete_flag = 0;

select

from es_role_cluster_host
where cluster = #{cluster} and delete_flag = 0;

mapper.update:
update es_role_cluster_host

    , role_cluster_id = #{roleClusterId}
    , `hostname` = #{hostname}
    , ip = #{ip}
    , cluster = #{cluster}
    , `port` = #{port}
    , `role` = #{role}
    , `status` = #{status}
    , rack = #{rack}
    , node_set = #{nodeSet}

where id = #{id} and delete_flag = 0;

mapper.select:
select

from es_role_cluster_host
where status = 1 and delete_flag = 0;

mapper.update:
update es_role_cluster_host
set delete_flag = 1
where cluster = #{cluster}

update es_role_cluster_host
set delete_flag = 0, status = 1
where hostname = #{hostname} and role_cluster_id = #{roleId} and delete_flag = 1;

update es_role_cluster_host
